import React, { Component } from 'react';
import Web3 from 'web3';
import Reset from './chairperson/reset.js';
import Set from './chairperson/set.js';
import Start from './chairperson/start.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      view:''
    };
  }

  componentDidMount() {
    this.loadBlockchainData();
  }

  async loadBlockchainData() {
    try {
      // Check if Web3 provider is available from Metamask or similar extension
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        await window.ethereum.enable(); // Request user permission to connect
        const accounts = await web3.eth.getAccounts();
        this.setState({ account: accounts[0] });
      } else {
        console.log('Please install MetaMask or use a compatible browser extension.');
      }
    } catch (error) {
      console.error('Error loading blockchain data:', error);
    }
  }
  async set() {
    this.setState({view:'set'})

  }
  async start() {
    this.setState({view:'start'})
  }
  async reset() {
    this.setState({view:'reset'})
  }

  render() {
    return (
      <div>
        <h1>Hello, Chairperson</h1>
        <p>Your account: {this.state.account}</p>
        <div className='container small'>
          <div className='col'>
            <div className='row'>
            <button type="button" class="btn btn-success" onClick={()=>this.set()}>SET</button>
            </div>
            <br/>
            <div className='row'>
            <button type="button" class="btn btn-success" onClick={()=>this.start()}>START</button>
            </div>
            <br/>
            <div className='row'>
            <button type="button" class="btn btn-success" onClick={()=>this.reset()}>RESET</button>
            </div>
            <br/>
          </div>

        </div>
        {view=='set' && <Set/>}
        {view=='start' && <Start/>}
        {view=='reset' && <Reset/>}
      </div>
    );
  }
}

export default App;
