import React, { Component } from 'react';
import Web3 from 'web3';
import { ABI, address } from '../config';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: '',
      winners_length:0,
      winners:[],
    };
  }

  componentDidMount() {
    this.loadBlockchainData();
  }

  async loadBlockchainData() {
    try {
      // Check if Web3 provider is available from Metamask or similar extension
      if (window.ethereum) {
        const web3 = new Web3(window.ethereum);
        await window.ethereum.enable(); // Request user permission to connect
        const accounts = await web3.eth.getAccounts();
        this.setState({ account: accounts[0] });
        const scontract = new web3.eth.Contract(ABI, address);
        this.setState({ scontract }, async () => {
          // Callback function to ensure scontract is set before calling get_names()
          await this.reveal_winner();
          this.get_winners_length();
          this.get_winners();
        });
      } else {
        console.log('Please install MetaMask or use a compatible browser extension.');
      }
    } catch (error) {
      console.error('Error loading blockchain data:', error);
    }
  }
  async get_winners_length() {
    const {scontract} = this.state;
    const lth = await scontract.methods.get_winners_length().call();
    this.setState({winners_length:lth});
    console.log(this.state.winners_length);
  }

  async get_winners() {
    const {scontract,winners_length} = this.state;
    const winner_array = [];
    for(let i=0;i<winners_length;i++)
    {
      const data = await scontract.methods.get_winners(i).call();
      winner_array.push(data);
    }
    this.setState({winners:winner_array});
    console.log(this.state.winners);
  }
    async reveal_winner() {
      const {scontract} = this.state;
      await scontract.methods.reveal_winner().send({from:this.state.account});
    
    }
  render() {
    return (
      <div>
        <h1>REVEAL WINNER PAGE</h1>
        <p>Your account: {this.state.account}</p>
      </div>
    );
  }
}

export default App;
