  render() {
    return (
      <div>
        <h3>Manage time page</h3>
        <div className='container'>
            <ul>
                <div className='row gy-5'>
                <div className='col'>
                <li>
                <button type="button" class="btn btn-success" onClick={()=>this.start_register()} >Start Registration process</button>
                </li>
                </div>
                
                </div>
                <div className='row gy-5'>
                    <div className='col'><li>
                <button type="button" class="btn btn-success" onClick={()=>this.stop_register()} >Stop Registration process</button>
                </li></div>
                
                </div>
                
                <li>
                <button type="button" class="btn btn-success" onClick={()=>this.start_voting()} >Start Voting process</button>
                </li>
                <li>
                <button type="button" class="btn btn-success" onClick={()=>this.stop_voting()} >Stop Voting process</button>
                </li>
                <li>
                <button type="button" class="btn btn-success" onClick={()=>this.start_reveal()} >Start Revealing the winners</button>
                </li>
            </ul>
        </div>
      </div>
    );
  }
}

export default App;
