        <p>Your account: {this.state.account}</p>
        <div className='container'>
        <button type="button" class="btn btn-success" onClick={()=>this.start()} style={{marginRight:'40px'}}>Start</button>
        </div>
      </div>
    );
  }
}

export default App;
